import Products from './components/Shop/Products';

function App() {
  const isShow = useSelector((state) => state.ui.isShow)

  const item = useSelector((state) => state.item);
  return (
    <Layout>
      {isShow &&<Cart />}
      <Products />
    </Layout>
  );
}

export default App;
