import {redirect} from 'react-router-dom'
export function getToken(){
    const data = localStorage.getItem('token')
    return data;
}

export function TokenLoader(){
    return getToken();
}

export function CheckAuth(){
    if(!getToken()){
        return redirect('/auth');
    }

    return null;
}
